import React, { useState } from 'react'
import { BiSolidHide ,BiSolidShow  } from "react-icons/bi";
import { Link } from 'react-router-dom';

const LoginForm = () => {

    const[formData,setFormData]=useState({email:"",password:"",pass:"password"})
    function changeHandler(e){
        const{name,value}=e.target;
        setFormData((prevData)=>({
            ...prevData,
            [name]:value,
        }));
    }
    function loginHandler(e){
        e.preventDefault();
   
    }
    function passHandler(e){
        e.preventDefault();
        setFormData(prevData=>({
            ...prevData,
            pass:formData.pass==="password"?"text":"password",
        }))

    }

  return (
    <div>
        <form className='flex flex-col justify-center items-center'>
           <div className='flex flex-col'>
            <label for="email">Email <sup>*</sup></label>
                <input required type='text' id='email' name='email' placeholder='Enter your email'  value={formData.email} onChange={changeHandler}/>

                <label for="email">password <sup>*</sup></label>
                <div className='flex flex-row  relative rounded-lg'>
                    <input required  type={formData.pass} id='password' name='password' placeholder='Enter your password'  value={formData.password} onChange={changeHandler} />
                    <button onClick={passHandler} className='absolute  right-1  bg-transparent text-black mt-4'>
                    {formData.pass==="password"?(<BiSolidHide />):(<BiSolidShow/>)}
                    </button>
                    <Link>Forgot Password ?</Link>
                </div>
           </div>
            <button onClick={loginHandler}>Login</button>
        </form>
    </div>
  )
}

export default LoginForm
