import React, { useState } from 'react'
import toast from 'react-hot-toast';
import { BiSolidHide ,BiSolidShow  } from "react-icons/bi";
import { useNavigate } from 'react-router-dom';


const SignupForm = () => {
  let navigate = useNavigate()
  const[showPassword,setShowPassword]=useState(false);
  const[formData,setFormData]=useState({firstName:"",lastName:"",email:"",password:"",confirmPassword:""})
  function changeHandler(e){
    e.preventDefault();
    setFormData((prevData)=>({
      ...prevData,
      [e.target.name]:e.target.value
    }))
  }
  function submitHandler(e){
    e.preventDefault();
    toast.success("SignUp Successfull")
  }
  return (
    <div>
      <div>
        <button>Student</button>
        <button>Instructor</button>
      </div>
      <form onSubmit={submitHandler} className='flex flex-col'>
        <div className='flex flex-row w-[30rem] border-4'>
          <label >First Name<sup>*</sup>
            <input
              required
              type='text'
              name='firstName'
              onChange={changeHandler}
              value={formData.firstName}
              placeholder='Enter First Name'
            ></input>
            </label>
          

          
          <label>Last Name<sup>*</sup>
          
            <input
              required
              type='text'
              name='lastName'
              onChange={changeHandler}
              value={formData.lastName}
              placeholder='Enter Last Name'

            ></input>
          </label>
          </div>
        <div className='flex flex-col'>
        <label>Email<sup>*</sup>
        <br/>
          <input
            required
            type='text'
            name='email'
            onChange={changeHandler}
            value={formData.email}
            placeholder='Enter Email'

          ></input>
        </label>
        </div>
        <div className='flex flex-row w-[30rem] border-4' >
          <label className='relative'>Create Password<sup>*</sup>
            <input
              required
              type={showPassword?"text":"password"}
              name='password'
              onChange={changeHandler}
              value={formData.password}
              placeholder='Password'

            ></input>
            <span onClick={()=>setShowPassword((prevState)=>!prevState)} className='absolute right-3 cursor-pointer top-4 text-xl' >{showPassword?(<BiSolidShow/>):(<BiSolidHide />)}</span>
          </label>

          <label className='relative'>Confirm Password<sup>*</sup>
            <input
              required
              type={showPassword?"text":"password"}
              name='confirmPassword'
              onChange={changeHandler}
              value={formData.confirmPassword}
              placeholder='Confirm Password'

            ></input>
            <span onClick={()=>setShowPassword((prevState)=>!prevState)} className='absolute right-3 cursor-pointer top-4 text-xl' >{showPassword?(<BiSolidShow/>):(<BiSolidHide />)}</span>

          </label>

        </div>
        
          <button>Create Account</button>
      </form>
    </div>
  )
}

export default SignupForm
