import React, { useState } from 'react'
import { BiSolidHide ,BiSolidShow  } from "react-icons/bi";

const LoginForm = () => {

    const[formData,setFormData]=useState({email:"",password:"",pass:"password"})
    function changeHandler(e){
        const{name,value}=e.target;
        setFormData((prevData)=>({
            ...prevData,
            [name]:value
        }));
    }
    function loginHandler(e){
        e.preventDefault();
   
    }
    function passHandler(e){
        e.preventDefault();
        setFormData(prevData=>({
            ...prevData,
            pass:formData.pass==="password"?"text":"password",
        }))

    }
    console.log(formData.pass)

  return (
    <div>
        <form className='flex flex-col justify-center items-center'>
           <div className='flex flex-col'>
            <label for="email">Email</label>
                <input type='text' id='email' name='email' value={formData.email} onChange={changeHandler}/>

                <label for="email">password</label>
                <div className='flex flex-row'>
                    <input type={formData.pass} id='password' name='password' value={formData.password} onChange={changeHandler}/>
                    <button onClick={passHandler}><BiSolidHide />
                    </button>
                </div>
           </div>
            <button onClick={loginHandler}>Login</button>
        </form>
    </div>
  )
}

export default LoginForm
